int main(void)
{
    int i, n;
    int fib[MAXN] = {1, 1};         /* 初始化前两个 */
    printf ("Enter n: ");    
    scanf ("%d", &amp;n);
    if(n &gt;= 1 &amp;&amp; n &lt;= 46 ){
        for(i = 2; i &lt; n; i++){  
            fib[i] = fib[i - 1] + fib[i - 2];
        }  
        /* 学学人家怎么输出：*/
        for(i = 0; i &lt; n; i++){
            printf("%6d", fib[i]);
            if((i + 1) % 5 == 0){   
            /* 每5个换行：循环变量i+1是5的倍数；注意细节i+1 */
                printf("\n");
            }  
        }
        if(n % 5 != 0){
        /* 最后不满5个换行：数学转化：最后一行和总数mod5同余 */ 
            printf("\n");
        }
    }else{
        printf("Invalid Value.\n"); 
    }
    return 0;
}
